import { useState } from "react";

const soundLibrary = [
  { id: "bass", name: "Bass", defaultBands: ["low", "low-mid"] },
  { id: "keys", name: "Keys", defaultBands: ["low-mid", "mid", "high"] },
  { id: "strings", name: "Strings", defaultBands: ["mid", "high"] },
  { id: "bell", name: "Bell", defaultBands: ["mid", "high"] },
  { id: "flute", name: "Flute", defaultBands: ["mid", "high"] },
  //{id:"" ,name:"",defaultBands:["",""]}
  //Flute
  //Guitar
  //Key
  //Organ
  //Lead
  //Pad
  //Pluck
  //Synth
  //Ambience
  //Brass
  //Percussion
  //Arp
];

const frequencyBands = ["low", "low-mid", "mid", "high"];

export default function App() {
  const [selectedRows, setSelectedRows] = useState([]);
  const totals = frequencyBands.reduce((acc, band) => {
    acc[band] = 0;
    return acc;
  }, {});

  selectedRows.forEach((row) => {
    row.bands.forEach((band) => {
      totals[band]++;
    });
  });

  function addSound(soundId) {
    const sound = soundLibrary.find((sound) => sound.id === soundId);

    const newRow = {
      rowId: crypto.randomUUID(),
      soundId: sound.id,
      bands: [...sound.defaultBands],
    };
    setSelectedRows((rows) => [...rows, newRow]);
  }

  function toggleBand(rowId, band) {
    setSelectedRows((rows) =>
      rows.map((row) => {
        if (row.rowId !== rowId) return row;
        const hasBand = row.bands.includes(band);

        const nextBands = hasBand
          ? row.bands.filter((b) => b !== band)
          : [...row.bands, band];

        return { ...row, bands: nextBands };
      }),
    );
  }

  return (
    <div>
      <h1 className="questrial-regular">Sound Tracker</h1>
      <h2>Sound Library</h2>

      <ul>
        {soundLibrary.map((sound) => (
          <li key={sound.id}>
            {sound.name}{" "}
            <button onClick={() => addSound(sound.id)}>Add</button>{" "}
          </li>
        ))}
      </ul>
      <h2>Selected Sounds</h2>
      {selectedRows.length === 0 ? (
        <p>No Sounds selected, add some sounds!</p>
      ) : (
        <ul>
          {selectedRows.map((row) => {
            const sound = soundLibrary.find(
              (sound) => sound.id === row.soundId,
            );

            return (
              <li key={row.rowId} style={{ marginBottom: 12 }}>
                <strong>{sound?.name ?? row.soundId}</strong>

                <div>
                  {frequencyBands.map((band) => (
                    <label key={band} style={{ marginRight: 12 }}>
                      <input
                        type="checkbox"
                        className="freq"
                        checked={row.bands.includes(band)}
                        onChange={() => toggleBand(row.rowId, band)}
                      />
                    </label>
                  ))}
                </div>
              </li>
            );
          })}
        </ul>
      )}
      <h2>Frequency Totals</h2>
      <ul>
        {frequencyBands.map((band) => (
          <li key={band}>
            {band}: {totals[band]}
          </li>
        ))}
      </ul>
    </div>
  );
}
