import { useState } from "react";

const soundLibrary = [
  { id: "bass", name: "Bass", defaultBands: ["low", "low-mid"] },
  { id: "piano", name: "Piano", defaultBands: ["low-mid", "mid", "high"] },
];

const frequencyBands = ["low", "low-mid", "mid", "high"];

export default function App() {
  const [selectedRows, setSelectedRows] = useState([]);

  function addSound(soundId) {
    const sound = soundLibrary.find((sound) => sound.id === soundId);

    const newRow = {
      rowId: crypto.randomUUID(),
      soundId: sound.id,
      bands: [...sound.defaultBands],
    };
    setSelectedRows((rows) => [...rows, newRow]);
  }

  return (
    <div>
      <h1>Sound Tracker</h1>
      <h2>Sound Library</h2>
      <ul>
        <ul>
          {soundLibrary.map((sound) => (
            <li key={sound.id}>
              {sound.name}{" "}
              <button onClick={() => addSound(sound.id)}>Add</button>{" "}
            </li>
          ))}
        </ul>
      </ul>
      <h2>Selected Sounds</h2>
      {selectedRows.length === 0 ? (
        <p>No Sounds selected, add some sounds!</p>
      ) : (
        <ul>
          {selectedRows.map((row) => (
            <li key={row.rowId}>{row.soundId}</li>
          ))}
        </ul>
      )}
    </div>
  );
}
